import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  standalone: true,
  template: `
    <footer class="footer">
      <p>Maltalist © 2025</p>
    </footer>
  `,
  styles: [`
    .footer {
      color: #034d88;
      text-align: center;
      padding: 18px 10px 12px 10px;
      font-size: 0.98rem;
      letter-spacing: 0.02em;
      flex-shrink: 0;
      margin-top: 40px;
      border-radius: 0 0 12px 12px;
    }
    .footer p {
      margin: 0;
      line-height: 1.6;
    }
    @media (max-width: 430px) {
      .footer {
        font-size: 0.88rem;
        border-radius: 0 0 4px 4px;
      }
    }
  `]
})
export class FooterComponent {}
