import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';
import { ListingService, ListingSummary } from '../../services/listing.service';

@Component({
  selector: 'app-profile',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div id="user-profile-page" *ngIf="currentUser">
      <div id="profile-details">
        <div id="profile-info">
          <img [src]="currentUser.picture || '/assets/img/placeholder.png'" [alt]="currentUser.name">
          <h2>{{ currentUser.name }}</h2>
          <p>{{ currentUser.email }}</p>
          <button id="create" (click)="navigateToCreate()">
            <span class="material-symbols-outlined">add</span>
            Create Listing
          </button>
          <button id="edit-profile-btn" (click)="toggleEditMode()">
            <span class="material-symbols-outlined">edit</span>
            Edit Profile
          </button>
          <button id="logout-btn" (click)="logout()">
            <span class="material-symbols-outlined">logout</span>
            Log Out
          </button>
        </div>

        <div id="profile-listings">
          <h2>My Listings</h2>
          <ul *ngIf="userListings.length > 0; else noListings">
            <li *ngFor="let listing of userListings" class="profile-listing">
              <img [src]="getMainImage(listing)" [alt]="listing.title" (click)="navigateToListing(listing.id)">
              <div class="listing-info">
                <h3 (click)="navigateToListing(listing.id)" class="profile-listing-link">{{ listing.title }}</h3>
                <p>€{{ listing.price }}</p>
                <p>{{ listing.category }}</p>
                <p>{{ listing.location }}</p>
              </div>
            </li>
          </ul>
          <ng-template #noListings>
            <p>You haven't created any listings yet.</p>
          </ng-template>
        </div>
      </div>
    </div>

    <div *ngIf="!currentUser" class="no-user">
      <h1>No User Logged In</h1>
      <div id="g_id_onload"
           data-client_id="763140433455-9tudkmcpnbec0dv4ndej56r1kho6hd3o.apps.googleusercontent.com"
           data-callback="onGoogleSignIn"
           data-auto_prompt="false">
      </div>
      <div class="g_id_signin"
           data-type="standard"
           data-shape="rectangular"
           data-theme="outline"
           data-text="sign_in_with"
           data-size="large"
           data-logo_alignment="left">
      </div>
    </div>
  `,
  styles: [`
    #user-profile-page {
      width: 100%;
    }

    #profile-details {
      display: flex;
      flex-flow: row;
      align-items: flex-start;
      justify-content: space-around;
      gap: 40px;
    }

    #profile-info {
      width: 25%;
      padding-top: 10px;
    }

    #profile-info img {
      border-radius: 8px;
      height: 150px;
      width: 150px;
      object-fit: cover;
      margin-bottom: 20px;
    }

    #profile-info h2 {
      color: #004779;
      margin-bottom: 10px;
    }

    #profile-info p {
      color: #666;
      margin-bottom: 20px;
    }

    #profile-info button {
      width: 100%;
      border-width: 2px;
      border-radius: 6px;
      padding: 8px 18px;
      font-size: 1rem;
      cursor: pointer;
      transition: background 0.2s, color 0.2s, border-color 0.2s;
      background: none;
      margin-bottom: 10px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 8px;
    }

    #profile-info button .material-symbols-outlined {
      font-size: 18px;
    }

    #create {
      border-color: #179944d4;
      color: #179944d4;
    }
    #create:hover {
      background-color: #179944db;
      color: #fefefe;
      border-color: transparent;
    }

    #edit-profile-btn {
      border-color: #e67b00d7;
      color: #e67b00d7;
    }
    #edit-profile-btn:hover {
      background-color: #e67b00dd;
      color: #fefefe;
      border-color: transparent;
    }

    #logout-btn {
      border-color: #b71c1ce5;
      color: #b71c1ce5;
    }
    #logout-btn:hover {
      background-color: #b71c1cd8;
      color: #fefefe;
      border-color: transparent;
    }

    #profile-listings {
      width: 60%;
    }

    #profile-listings h2 {
      color: #004779;
      margin-bottom: 20px;
    }

    #profile-listings ul {
      display: flex;
      flex-wrap: wrap;
      gap: 20px;
      padding: 0;
      margin: 0;
    }

    #profile-listings li {
      list-style: none;
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 200px;
      text-align: center;
      border-radius: 8px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.1);
      padding: 15px;
      transition: box-shadow 0.2s;
    }

    #profile-listings li:hover {
      box-shadow: 0 4px 16px rgba(0,0,0,0.15);
    }

    #profile-listings img {
      width: 100%;
      height: 120px;
      border-radius: 8px;
      border: 1px solid #ddd;
      background: #fafafa;
      object-fit: cover;
      cursor: pointer;
      margin-bottom: 10px;
    }

    #profile-listings h3 {
      margin: 10px 0;
      font-size: 1.2em;
      cursor: pointer;
      color: #004779;
      transition: color 0.2s;
    }

    #profile-listings h3:hover {
      color: #004779;
    }

    #profile-listings p {
      font-size: 0.9em;
      color: #555;
      margin: 2px 0;
    }

    .profile-listing {
      display: flex;
      align-items: center;
      gap: 10px;
      margin-bottom: 10px;
      width: 100%;
    }

    .listing-info {
      color: #004779;
      display: flex;
      flex-flow: column;
      flex: 1;
    }

    .profile-listing-link {
      text-decoration: none;
      cursor: pointer;
    }

    .no-user {
      text-align: center;
      padding: 50px 20px;
    }

    .no-user h1 {
      color: #004779;
      margin-bottom: 30px;
    }

    @media (max-width: 700px) {
      #profile-details {
        flex-flow: column;
        gap: 18px;
      }

      #profile-info {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        text-align: center;
      }

      #profile-info img {
        margin: 0 auto;
      }

      #profile-listings {
        width: 100%;
      }
    }
  `]
})
export class ProfileComponent implements OnInit {
  currentUser: User | null = null;
  userListings: ListingSummary[] = [];
  isEditMode = false;

  constructor(
    private authService: AuthService,
    private listingService: ListingService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit() {
    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
      if (user) {
        this.loadUserListings(user.id);
      }
    });

    // Set up Google Sign-in callback for non-authenticated users
    (window as any).onGoogleSignIn = (response: any) => {
      this.authService.handleGoogleSignIn(response).subscribe({
        next: (user) => {
          if (user && user.id) {
            this.router.navigate(['/profile', user.id]);
          }
        },
        error: (err) => {
          console.error('Google login error:', err);
          alert('Google login failed.');
        }
      });
    };
  }

  loadUserListings(userId: string) {
    this.listingService.getUserListings(userId).subscribe({
      next: (listings) => {
        this.userListings = listings;
      },
      error: (error) => {
        console.error('Error loading user listings:', error);
      }
    });
  }

  navigateToCreate() {
    this.router.navigate(['/create']);
  }

  navigateToListing(id: number) {
    this.router.navigate(['/listing', id]);
  }

  toggleEditMode() {
    this.isEditMode = !this.isEditMode;
    // TODO: Implement edit profile functionality
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/']);
  }

  getMainImage(listing: ListingSummary): string {
    return listing.picture1 || '/assets/img/placeholder.png';
  }
}
