import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { ListingService, Listing } from '../../services/listing.service';
import { AuthService, User } from '../../services/auth.service';

@Component({
  selector: 'app-listing-details',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div id="listing-details" *ngIf="listing">
      <div class="listing-images">
        <div class="carousel-container">
          <div class="carousel-main">
            <img
              id="carousel-big-img"
              [src]="currentImage"
              alt="Listing image"
              *ngIf="listing.picture1">
            <div *ngIf="!listing.picture1" class="no-image">
              No images available
            </div>
          </div>
        </div>
      </div>

      <div class="listing-details-info">
        <h2>{{ listing.title }}</h2>
        <p class="price">€{{ listing.price }}</p>
        <p class="category"><strong>Category:</strong> {{ listing.category }}</p>
        <p class="location"><strong>Location:</strong> {{ listing.location }}</p>
        <p class="description">{{ listing.description }}</p>

        <div class="listing-meta">
          <div>
            <span class="material-symbols-outlined">schedule</span>
            {{ formatDate(listing.createdAt) }}
          </div>
          <div *ngIf="listing.isPromoted" class="promoted-badge">Promoted</div>
        </div>

        <div class="listing-buttons">
          <button
            id="listing-contact"
            (click)="contactSeller()"
            *ngIf="!isOwner">
            <span class="material-symbols-outlined">contact_mail</span>
            Contact Seller
          </button>

          <div *ngIf="isOwner" class="owner-buttons">
            <button id="edit-listing-btn" (click)="editListing()">
              <span class="material-symbols-outlined">edit</span>
              Edit Listing
            </button>
            <button id="delete-listing-btn" (click)="deleteListing()">
              <span class="material-symbols-outlined">delete</span>
              Delete Listing
            </button>
          </div>
        </div>

        <button class="copy-btn" (click)="copyToClipboard()">
          <span class="material-symbols-outlined">content_copy</span>
          Copy Link
        </button>
      </div>
    </div>

    <div *ngIf="!listing" class="loading">
      <p>Loading listing details...</p>
    </div>
  `,
  styles: [`
    #listing-details {
      display: flex;
      flex-flow: row nowrap;
      gap: 32px;
      align-items: flex-start;
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
    }

    .listing-images {
      width: 50%;
    }

    .carousel-container {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      margin-bottom: 20px;
      width: 100%;
    }

    .carousel-main {
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 100%;
    }

    .carousel-main img#carousel-big-img {
      width: 100%;
      max-width: 500px;
      height: auto;
      object-fit: contain;
      border-radius: 8px;
      border: 1px solid #ddd;
      background: #fafafa;
    }

    .no-image {
      width: 100%;
      max-width: 500px;
      height: 300px;
      background: #e9ecef;
      display: flex;
      align-items: center;
      justify-content: center;
      color: #666;
      border-radius: 8px;
      border: 1px solid #ddd;
    }

    .carousel-thumbnails {
      display: flex;
      flex-direction: row;
      gap: 10px;
      margin-top: 15px;
      justify-content: center;
      width: 100%;
      flex-wrap: wrap;
    }

    .carousel-thumb {
      width: 70px;
      height: 70px;
      object-fit: cover;
      border-radius: 5px;
      border: 2px solid transparent;
      cursor: pointer;
      transition: border 0.2s;
    }

    .carousel-thumb.selected {
      border: 2px solid #034d88;
    }

    .listing-details-info {
      width: 50%;
      padding: 20px;
      background: #fff;
      border-radius: 12px;
      box-shadow: 0 4px 32px rgba(0,0,0,0.07);
    }

    .listing-details-info h2 {
      color: #004779;
      margin-bottom: 10px;
    }

    .price {
      font-size: 1.5rem;
      font-weight: bold;
      color: #004779;
      margin-bottom: 15px;
    }

    .category, .location {
      margin: 8px 0;
      color: #555;
    }

    .description {
      margin: 20px 0;
      line-height: 1.6;
      color: #333;
    }

    .listing-meta {
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: 0.9rem;
      color: #666;
      padding: 10px 0;
      border-top: 1px solid #eee;
      margin-top: 20px;
    }

    .listing-meta div {
      display: flex;
      align-items: center;
      gap: 4px;
    }

    .promoted-badge {
      background: gold;
      color: #333;
      padding: 2px 6px;
      border-radius: 4px;
      font-size: 0.8em;
      font-weight: bold;
    }

