import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { ListingService, ListingSummary, GetAllListingsResponse } from '../../services/listing.service';

interface FilterOption {
  category: string;
  icon: string;
  label: string;
  selected: boolean;
}

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  filters: FilterOption[] = [
    { category: '', icon: 'apps', label: 'All', selected: true },
    { category: 'Electronics', icon: 'devices', label: 'Electronics', selected: false },
    { category: 'Furniture', icon: 'chair', label: 'Furniture', selected: false },
    { category: 'Clothing', icon: 'apparel', label: 'Clothing', selected: false },
    { category: 'Vehicles', icon: 'pedal_bike', label: 'Vehicles', selected: false },
    { category: 'Real Estate', icon: 'real_estate_agent', label: 'Real Estate', selected: false },
    { category: 'Sports&Hobby', icon: 'sports_tennis', label: 'Sports&Hobby', selected: false },
    { category: 'Books', icon: 'auto_stories', label: 'Books', selected: false },
    { category: 'Other', icon: 'construction', label: 'Other', selected: false }
  ];

  locations: string[] = [
    'Valletta', 'Sliema', 'St. Julian\'s', 'Birkirkara', 'Mosta', 'Qormi', 'Zebbug', 'Attard', 'Balzan',
    'Birzebbuga', 'Fgura', 'Floriana', 'Gzira', 'Hamrun', 'Marsaskala', 'Marsaxlokk', 'Mdina', 'Mellieha',
    'Msida', 'Naxxar', 'Paola', 'Pembroke', 'Rabat', 'San Gwann', 'Santa Venera', 'Siggiewi', 'Swieqi',
    'Tarxien', 'Vittoriosa', 'Xghajra', 'Zabbar', 'Zejtun', 'Zurrieq',
    'Gozo - Victoria', 'Gozo - Xewkija', 'Gozo - Nadur', 'Gozo - Qala', 'Gozo - Ghajnsielem',
    'Gozo - Xaghra', 'Gozo - Sannat', 'Gozo - Munxar', 'Gozo - Fontana', 'Gozo - Gharb',
    'Gozo - San Lawrenz', 'Gozo - Zebbug'
  ];

  listings: ListingSummary[] = [];
  loading = false;
  currentPage = 1;
  totalPages = 1;
  pageSize = 9;

  searchQuery = '';
  selectedLocation = '';
  selectedSort = 'date_desc';
  selectedCategory = '';

  private searchTimeout: any;

  constructor(
    private listingService: ListingService,
    private router: Router
  ) {}

  ngOnInit() {
    this.loadListings();
  }

  selectFilter(filter: FilterOption) {
    this.filters.forEach(f => f.selected = false);
    filter.selected = true;
    this.selectedCategory = filter.category;
    this.currentPage = 1;
    this.loadListings();
  }

  onSearchInput() {
    if (this.searchTimeout) {
      clearTimeout(this.searchTimeout);
    }
    this.searchTimeout = setTimeout(() => {
      this.currentPage = 1;
      this.loadListings();
    }, 300);
  }

  onLocationChange() {
    this.currentPage = 1;
    this.loadListings();
  }

  onSortChange() {
    this.currentPage = 1;
    this.loadListings();
  }

  changePage(page: number) {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage = page;
      this.loadListings();
    }
  }

  loadListings() {
    this.loading = true;
    this.listingService.getAllListings({
      page: this.currentPage,
      pageSize: this.pageSize,
      category: this.selectedCategory,
      location: this.selectedLocation,
      search: this.searchQuery,
      sort: this.selectedSort
    }).subscribe({
      next: (response: GetAllListingsResponse) => {
        this.listings = response.listings;
        this.totalPages = Math.ceil(response.totalNumber / this.pageSize);
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading listings:', error);
        this.loading = false;
      }
    });
  }

  navigateToListing(id: number) {
    this.router.navigate(['/listing', id]);
  }

  getMainImage(listing: ListingSummary): string {
    return listing.picture1 || '';
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  }
}
<div id="listings-page">
  <div id="tools">
    <div id="filter-container">
      <div
        *ngFor="let filter of filters"
        class="filter-option"
        [class.selected]="filter.selected"
        (click)="selectFilter(filter)">
        <span class="material-symbols-outlined">{{ filter.icon }}</span>
        <span>{{ filter.label }}</span>
      </div>
    </div>
    <div id="search-bar">
      <input
        type="text"
        id="search"
        [(ngModel)]="searchQuery"
        (input)="onSearchInput()"
        placeholder="Search listings...">
    </div>
  </div>

  <div id="sort-full">
    <select id="location-select" [(ngModel)]="selectedLocation" (change)="onLocationChange()">
      <option value="">Select a city</option>
      <option *ngFor="let location of locations" [value]="location">{{ location }}</option>
    </select>
    <select id="sort" [(ngModel)]="selectedSort" (change)="onSortChange()">
      <option value="date_desc">Newest First</option>
      <option value="date_asc">Oldest First</option>
      <option value="price_asc">Price Low to High</option>
      <option value="price_desc">Price High to Low</option>
      <option value="distance">Distance</option>
    </select>
  </div>

  <div id="listing-list">
    <div *ngIf="loading" class="loading">Loading listings...</div>
    <div *ngIf="!loading && listings.length === 0" class="no-listings">No listings found</div>
    <ul *ngIf="!loading && listings.length > 0">
      <li *ngFor="let listing of listings" (click)="navigateToListing(listing.id)" [class.promoted]="listing.isPromoted">
        <div>
          <img [src]="getMainImage(listing)" [alt]="listing.title" *ngIf="listing.picture1">
          <div *ngIf="!listing.picture1" class="no-image">No Image</div>
          <h3>{{ listing.title }}</h3>
          <p class="price">€{{ listing.price }}</p>
          <p class="category">{{ listing.category }}</p>
          <p class="location">{{ listing.location }}</p>
          <div class="listing-meta">
            <div>
              <span class="material-symbols-outlined">schedule</span>
              {{ formatDate(listing.createdAt) }}
            </div>
          </div>
          <div *ngIf="listing.isPromoted" class="promoted-badge">Promoted</div>
        </div>
      </li>
    </ul>
  </div>

  <div class="pagination" *ngIf="totalPages > 1">
    <button (click)="changePage(currentPage - 1)" [disabled]="currentPage === 1">Previous</button>
    <span>Page {{ currentPage }} of {{ totalPages }}</span>
    <button (click)="changePage(currentPage + 1)" [disabled]="currentPage === totalPages">Next</button>
  </div>
</div>
