import { Component, OnInit } from '@angular/core';
import { Router, RouterLink } from '@angular/router';
import { CommonModule } from '@angular/common';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  currentUser: any = null;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
    });

    // Set up Google Sign-in callback
    (window as any).onGoogleSignIn = (response: any) => {
      this.authService.handleGoogleSignIn(response).subscribe({
        next: (user) => {
          if (user && user.id) {
            this.router.navigate(['/profile', user.id]);
          }
        },
        error: (err) => {
          console.error('Google login error:', err);
          alert('Google login failed.');
        }
      });
    };
  }
}
<header class="navbar">
  <nav>
    <a routerLink="/" class="logo-link">
      <img src="/assets/img/logo.png" alt="MaltaListings" class="logo">
    </a>
    <a routerLink="/profile" *ngIf="currentUser; else loginButton" class="profile-link">
      <span class="material-symbols-outlined">account_circle</span>
      My Profile
    </a>
    <ng-template #loginButton>
      <div id="g_id_onload"
           data-client_id="763140433455-9tudkmcpnbec0dv4ndej56r1kho6hd3o.apps.googleusercontent.com"
           data-callback="onGoogleSignIn"
           data-auto_prompt="false">
      </div>
      <div class="g_id_signin"
           data-type="standard"
           data-shape="rectangular"
           data-theme="outline"
           data-text="sign_in_with"
           data-size="large"
           data-logo_alignment="left">
      </div>
    </ng-template>
  </nav>
</header>
