import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { ListingService, CreateListingRequest } from '../../services/listing.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-create-listing',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './create-listing.component.html',
  styleUrls: ['./create-listing.component.css']
})
export class CreateListingComponent {
  listing: CreateListingRequest = {
    name: '',
    description: '',
    price: 0,
    category: '',
    location: '',
    pictures: []
  };

  locations: string[] = [
    'Valletta', 'Sliema', 'St. Julian\'s', 'Birkirkara', 'Mosta', 'Qormi', 'Zebbug', 'Attard', 'Balzan',
    'Birzebbuga', 'Fgura', 'Floriana', 'Gzira', 'Hamrun', 'Marsaskala', 'Marsaxlokk', 'Mdina', 'Mellieha',
    'Msida', 'Naxxar', 'Paola', 'Pembroke', 'Rabat', 'San Gwann', 'Santa Venera', 'Siggiewi', 'Swieqi',
    'Tarxien', 'Vittoriosa', 'Xghajra', 'Zabbar', 'Zejtun', 'Zurrieq',
    'Gozo - Victoria', 'Gozo - Xewkija', 'Gozo - Nadur', 'Gozo - Qala', 'Gozo - Ghajnsielem',
    'Gozo - Xaghra', 'Gozo - Sannat', 'Gozo - Munxar', 'Gozo - Fontana', 'Gozo - Gharb',
    'Gozo - San Lawrenz', 'Gozo - Zebbug'
  ];

  pictureSlots = Array(10).fill(0);
  selectedFiles: (File | null)[] = Array(10).fill(null);
  previewUrls: string[] = Array(10).fill('');

  isSubmitting = false;
  errorMessage = '';

  constructor(
    private listingService: ListingService,
    private authService: AuthService,
    private router: Router
  ) {}

  onFileSelected(event: any, index: number) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFiles[index] = file;

      // Create preview URL
      const reader = new FileReader();
      reader.onload = (e) => {
        this.previewUrls[index] = e.target?.result as string;
      };
      reader.readAsDataURL(file);
    }
  }

  triggerFileInput(index: number) {
    const input = document.getElementById('picture-' + index) as HTMLInputElement;
    input.click();
  }

  onSubmit() {
    if (!this.authService.isAuthenticated()) {
      this.errorMessage = 'Please log in to create a listing.';
      return;
    }

    this.isSubmitting = true;
    this.errorMessage = '';

    // Filter out null files
    this.listing.pictures = this.selectedFiles.filter(file => file !== null) as File[];

    this.listingService.createListing(this.listing).subscribe({
      next: (createdListing) => {
        this.isSubmitting = false;
        this.router.navigate(['/listing', createdListing.id]);
      },
      error: (error) => {
        this.isSubmitting = false;
        this.errorMessage = 'Failed to create listing. Please try again.';
        console.error('Error creating listing:', error);
      }
    });
  }
}
<div class="create-listing-container">
  <h2>Create New Listing</h2>
  <form (ngSubmit)="onSubmit()" #listingForm="ngForm">
    <div class="form-wrapper">
      <div class="form-fields">
        <label for="name">Title:</label>
        <input type="text" id="name" name="name" [(ngModel)]="listing.name" required #name="ngModel">

        <label for="description">Description:</label>
        <textarea id="description" name="description" [(ngModel)]="listing.description" required #description="ngModel"></textarea>

        <label for="price">Price (EUR):</label>
        <input type="number" id="price" name="price" [(ngModel)]="listing.price" step="0.01" min="0" required #price="ngModel">

        <label for="category">Category:</label>
        <select id="category" name="category" [(ngModel)]="listing.category" required #category="ngModel">
          <option value="">Select a category</option>
          <option value="Electronics">Electronics</option>
          <option value="Furniture">Furniture</option>
          <option value="Clothing">Clothing</option>
          <option value="Vehicles">Vehicles</option>
          <option value="Real Estate">Real Estate</option>
          <option value="Sports&Hobby">Sports&Hobby</option>
          <option value="Books">Books</option>
          <option value="Other">Other</option>
        </select>

        <label for="location">Location:</label>
        <select id="location" name="location" [(ngModel)]="listing.location" required #location="ngModel">
          <option value="">Select a city</option>
          <option *ngFor="let location of locations" [value]="location">{{ location }}</option>
        </select>

        <button type="submit" [disabled]="!listingForm.form.valid || isSubmitting">
          {{ isSubmitting ? 'Creating...' : 'Create Listing' }}
        </button>
        <div id="form-message" *ngIf="errorMessage">{{ errorMessage }}</div>
      </div>

      <div class="picture-upload-section">
        <label>Pictures (up to 10):</label>
        <div id="picture-inputs">
          <div *ngFor="let i of pictureSlots; let index = index" class="picture-upload">
            <input
              type="file"
              [id]="'picture-' + index"
              [name]="'picture-' + index"
              accept="image/*"
              (change)="onFileSelected($event, index)"
              style="display: none;">
            <div
              class="picture-preview"
              [class.has-image]="selectedFiles[index]"
              (click)="triggerFileInput(index)">
              <img *ngIf="selectedFiles[index]" [src]="previewUrls[index]" alt="Preview">
              <span *ngIf="!selectedFiles[index]" class="material-symbols-outlined">add_photo_alternate</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  </form>
</div>
