import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { ListingService, Listing } from '../../services/listing.service';
import { AuthService, User } from '../../services/auth.service';

@Component({
  selector: 'app-listing-details',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './listing-details.component.html',
  styleUrls: ['./listing-details.component.css']
})
export class ListingDetailsComponent implements OnInit {
  listing: Listing | null = null;
  currentImageIndex = 0;
  currentUser: User | null = null;
  isOwner = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private listingService: ListingService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loadListing(id);
    }

    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
      this.checkOwnership();
    });
  }

  loadListing(id: string) {
    this.listingService.getListingById(id).subscribe({
      next: (listing) => {
        this.listing = listing;
        this.checkOwnership();
      },
      error: (error) => {
        console.error('Error loading listing:', error);
        // TODO: Handle error (show not found page)
      }
    });
  }

  checkOwnership() {
    if (this.listing && this.currentUser) {
      this.isOwner = this.listing.userId === this.currentUser.id;
    } else {
      this.isOwner = false;
    }
  }

  get currentImage(): string {
    if (this.listing && this.listing.picture1) {
      return this.listing.picture1;
    }
    return '';
  }

  selectImage(index: number) {
    this.currentImageIndex = index;
  }

  contactSeller() {
    if (this.listing) {
      // TODO: Implement contact functionality
      // This could open a modal, redirect to a contact form, or use Stripe for payments
      alert('Contact functionality will be implemented with Stripe integration');
    }
  }

  editListing() {
    if (this.listing) {
      // TODO: Navigate to edit page or open edit modal
      alert('Edit functionality will be implemented');
    }
  }

  deleteListing() {
    if (this.listing && confirm('Are you sure you want to delete this listing?')) {
      this.listingService.deleteListing(this.listing.id.toString()).subscribe({
        next: () => {
          this.router.navigate(['/']);
        },
        error: (error) => {
          console.error('Error deleting listing:', error);
          alert('Failed to delete listing. Please try again.');
        }
      });
    }
  }

  copyToClipboard() {
    const url = window.location.href;
    navigator.clipboard.writeText(url).then(() => {
      // TODO: Show success message
      alert('Link copied to clipboard!');
    }).catch(err => {
      console.error('Failed to copy link:', err);
    });
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  }
}
<div id="listing-details" *ngIf="listing">
  <div class="listing-images">
    <div class="carousel-container">
      <div class="carousel-main">
        <img
          id="carousel-big-img"
          [src]="currentImage"
          alt="Listing image"
          *ngIf="listing.picture1">
        <div *ngIf="!listing.picture1" class="no-image">
          No images available
        </div>
      </div>
    </div>
  </div>

  <div class="listing-details-info">
    <h2>{{ listing.title }}</h2>
    <p class="price">€{{ listing.price }}</p>
    <p class="category"><strong>Category:</strong> {{ listing.category }}</p>
    <p class="location"><strong>Location:</strong> {{ listing.location }}</p>
    <p class="description">{{ listing.description }}</p>

    <div class="listing-meta">
      <div>
        <span class="material-symbols-outlined">schedule</span>
        {{ formatDate(listing.createdAt) }}
      </div>
      <div *ngIf="listing.isPromoted" class="promoted-badge">Promoted</div>
    </div>

    <div class="listing-buttons">
      <button
        id="listing-contact"
        (click)="contactSeller()"
        *ngIf="!isOwner">
        <span class="material-symbols-outlined">contact_mail</span>
        Contact Seller
      </button>

      <div *ngIf="isOwner" class="owner-buttons">
        <button id="edit-listing-btn" (click)="editListing()">
          <span class="material-symbols-outlined">edit</span>
          Edit Listing
        </button>
        <button id="delete-listing-btn" (click)="deleteListing()">
          <span class="material-symbols-outlined">delete</span>
          Delete Listing
        </button>
      </div>
    </div>

    <button class="copy-btn" (click)="copyToClipboard()">
      <span class="material-symbols-outlined">content_copy</span>
      Copy Link
    </button>
  </div>
</div>

<div *ngIf="!listing" class="loading">
  <p>Loading listing details...</p>
</div>
